import React, { Component } from "react";
import {
    Grid,
    IconButton,
    Icon,
    TablePagination,
    Button,
    TextField,
    InputAdornment,
    Input,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import SearchIcon from "@material-ui/icons/Search";
import MaterialTable, {
    MTableToolbar,
    Chip,
    MTableBody,
    MTableHeader,
} from "material-table";
import {
    findUserByUserName,
    searchByPage,
    getItemById,
    getListHealthOrgByUser,
    SearchUserByUserName,
    searchByDto,
} from "./UserService";
import UserEditorDialog from "./UserEditorDialog";
import { Breadcrumb, ConfirmationDialog } from "egret";
import { useTranslation, withTranslation, Trans } from "react-i18next";
import shortid from "shortid";
import { saveAs } from "file-saver";

function MaterialButton(props) {
    const { t, i18n } = useTranslation();
    const item = props.item;
    return (
        <div>
            <IconButton size="small" onClick={() => props.onSelect(item, 0)}>
                <Icon fontSize="small" color="primary">
                    edit
                </Icon>
            </IconButton>
            {/* <IconButton onClick={() => props.onSelect(item, 1)}>
      <Icon color="error">delete</Icon>
    </IconButton> */}
        </div>
    );
}

class User extends Component {
    state = {
        keyword: "",
        rowsPerPage: 10,
        page: 0,
        eQAHealthOrgType: [],
        item: {},
        shouldOpenEditorDialog: false,
        shouldOpenConfirmationDialog: false,
        selectAllItem: false,
        selectedList: [],
        totalElements: 0,
        shouldOpenConfirmationDeleteAllDialog: false,
    };
    numSelected = 0;
    rowCount = 0;

    handleTextChange = (event) => {
        this.setState({ keyword: event.target.value }, function () {});
    };

    handleKeyDownEnterSearch = (e) => {
        if (e.key === "Enter") {
            this.search();
        }
    };

    setPage = (page) => {
        this.setState({ page }, function () {
            this.updatePageData();
        });
    };

    setRowsPerPage = (event) => {
        this.setState(
            { rowsPerPage: event.target.value, page: 0 },
            function () {
                this.updatePageData();
            }
        );
    };

    handleChangePage = (event, newPage) => {
        this.setPage(newPage);
    };

    // search() {
    //   this.setState({ page: 0 }, function () {
    //     var searchObject = {};
    //     searchObject.text = this.state.keyword;
    //     searchObject.pageIndex = 1;
    //     searchObject.pageSize = this.state.rowsPerPage;
    //     findUserByUserName(searchObject.text, searchObject.pageIndex, searchObject.pageSize).then(({ data }) => {
    //       this.setState({ itemList: [...data.content], totalElements: data.totalElements })
    //     });
    //   });
    // }
    search() {
        this.setState({ page: 0 }, () => {
            var searchObject = {};
            searchObject.text = this.state.keyword.trim();
            searchObject.pageIndex = this.state.page + 1;
            searchObject.pageSize = this.state.rowsPerPage;
            searchByPage(searchObject.pageIndex, searchObject.pageSize).then(
                ({ data }) => {
                    this.setState({
                        itemList: [...data.content],
                        totalElements: data.totalElements,
                    });
                }
            );
        });
    }

    updatePageData = () => {
        var searchObject = {};
        //searchObject.text = this.state.keyword;
        searchObject.text = this.state.keyword.trim();
        searchObject.pageIndex = this.state.page + 1;
        searchObject.pageSize = this.state.rowsPerPage;

        searchByPage(searchObject.pageIndex, searchObject.pageSize).then(
            ({ data }) => {
                this.setState({
                    itemList: [...data.content],
                    totalElements: data.totalElements,
                });
            }
        );
    };

    handleDownload = () => {
        var blob = new Blob(["Hello, world!"], {
            type: "text/plain;charset=utf-8",
        });
        saveAs(blob, "hello world.txt");
    };
    handleDialogClose = () => {
        this.setState({
            shouldOpenEditorDialog: false,
            shouldOpenConfirmationDialog: false,
            shouldOpenConfirmationDeleteAllDialog: false,
        });
        this.setPage(0);
    };

    handleOKEditClose = () => {
        this.setState({
            shouldOpenEditorDialog: false,
            shouldOpenConfirmationDialog: false,
        });
        this.updatePageData();
    };

    handleDeleteUser = (id) => {
        this.setState({
            id,
            shouldOpenConfirmationDialog: true,
        });
    };

    handleEditUser = (item) => {
        getItemById(item.id).then((result) => {
            this.setState({
                item: result.data,
                shouldOpenEditorDialog: true,
            });
        });
    };

    componentDidMount() {
        this.updatePageData();
    }

    handleEditItem = (item) => {
        this.setState({
            item: item,
            shouldOpenEditorDialog: true,
        });
    };

    handleDelete = (id) => {
        this.setState({
            id,
            shouldOpenConfirmationDialog: true,
        });
    };

    handleDeleteAll = (event) => {
        this.handleDeleteList(this.data).then(() => {
            this.updatePageData();
            this.handleDialogClose();
        });
    };

    render() {
        const { t, i18n } = this.props;
        let {
            keyword,
            rowsPerPage,
            page,
            totalElements,
            itemList,
            item,
            shouldOpenConfirmationDialog,
            shouldOpenEditorDialog,
            shouldOpenConfirmationDeleteAllDialog,
        } = this.state;

        let columns = [
            {
                title: t("Action"),
                field: "custom",
                align: "left",
                width: "250",
                render: (rowData) => (
                    <MaterialButton
                        item={rowData}
                        onSelect={(rowData, method) => {
                            if (method === 0) {
                                getItemById(rowData.id).then(({ data }) => {
                                    if (data.parent === null) {
                                        data.parent = {};
                                    }
                                    this.setState({
                                        item: data,
                                        shouldOpenEditorDialog: true,
                                    });
                                });
                            } else if (method === 1) {
                                this.handleDelete(rowData.id);
                            } else {
                                alert("Call Selected Here:" + rowData.id);
                            }
                        }}
                    />
                ),
            },
            { title: t("username"), field: "username", width: "150" },
            {
                title: t("user.displayName"),
                field: "person.displayName",
                width: "150",
            },
            {
                title: t("general.email"),
                field: "email",
                align: "left",
                width: "150",
            },
        ];

        return (
            <div className="m-sm-30">
                <div className="mb-sm-30">
                    <Breadcrumb
                        routeSegments={[
                            {
                                name: t("Dashboard.manage"),
                                path: "/directory/apartment",
                            },
                            { name: t("user.title") },
                        ]}
                    />
                </div>

                <Grid container spacing={3}>
                    <Grid item lg={5} md={5} sm={5} xs={12}>
                        <Button
                            className="mb-16 mr-16 align-bottom"
                            variant="contained"
                            color="primary"
                            onClick={() => {
                                this.handleEditItem({
                                    startDate: new Date(),
                                    endDate: new Date(),
                                    isAddNew: true,
                                });
                            }}>
                            {t("Add")}
                        </Button>
                        {shouldOpenConfirmationDeleteAllDialog && (
                            <ConfirmationDialog
                                open={shouldOpenConfirmationDeleteAllDialog}
                                onConfirmDialogClose={this.handleDialogClose}
                                onYesClick={this.handleDeleteAll}
                                text={t("DeleteAllConfirm")}
                            />
                        )}
                    </Grid>
                    {/* <TextField
                        label={t("EnterSearch")}
                        className="mb-16 mr-10"
                        type="text"
                        name="keyword"
                        value={keyword}
                        onKeyDown={this.handleKeyDownEnterSearch}
                        onChange={this.handleTextChange}
                    />
                    <Button
                        className="mb-16 mr-16 align-bottom"
                        variant="contained"
                        color="primary"
                        onClick={() => this.search(keyword)}>
                        {t("Search")}
                    </Button>

                    <Grid item lg={7} md={7} sm={7} xs={12}>
                        <Input
                            label={t("EnterSearch")}
                            type="text"
                            name="keyword"
                            value={keyword}
                            onChange={this.handleTextChange}
                            onKeyDown={this.handleKeyDownEnterSearch}
                            className="w-100 mb-16 mr-10 stylePlaceholder"
                            id="search_box"
                            placeholder={t("general.enterSearch")}
                            startAdornment={
                                <InputAdornment>
                                    <Link to="#">
                                        <SearchIcon
                                            onClick={() => this.search(keyword)}
                                            style={{
                                                position: "absolute",
                                                top: "0",
                                                right: "0",
                                            }}
                                        />
                                    </Link>
                                </InputAdornment>
                            }
                        />
                          </Grid>*/}
                </Grid> 
                <Grid item xs={12}>
                    <div>
                        {shouldOpenEditorDialog && (
                            <UserEditorDialog
                                t={t}
                                i18n={i18n}
                                handleClose={this.handleDialogClose}
                                open={shouldOpenEditorDialog}
                                handleOKEditClose={this.handleOKEditClose}
                                item={item}
                            />
                        )}

                        {shouldOpenConfirmationDialog && (
                            <ConfirmationDialog
                                title={t("confirm")}
                                open={shouldOpenConfirmationDialog}
                                onConfirmDialogClose={this.handleDialogClose}
                                onYesClick={this.handleConfirmationResponse}
                                text={t("DeleteConfirm")}
                            />
                        )}
                    </div>
                    <MaterialTable
                        title={t("List")}
                        data={itemList}
                        columns={columns}
                        //parentChildData={(row, rows) => rows.find(a => a.id === row.parentId)}
                        parentChildData={(row, rows) => {
                            var list = rows.find((a) => a.id === row.parentId);
                            return list;
                        }}
                        // options={{
                        //   selection: false,
                        //   actionsColumnIndex: 0,
                        //   paging: false,
                        //   search: false
                        // }}
                        components={{
                            Toolbar: (props) => <MTableToolbar {...props} />,
                        }}
                        onSelectionChange={(rows) => {
                            this.data = rows;
                            // this.setState({selectedItems:rows});
                        }}
                        options={{
                            selection: false,
                            actionsColumnIndex: -1,
                            paging: false,
                            search: false,
                            rowStyle: (rowData, index) => ({
                                backgroundColor:
                                    index % 2 === 1 ? "#EEE" : "#FFF",
                            }),
                            maxBodyHeight: "450px",
                            minBodyHeight: "370px",
                            headerStyle: {
                                backgroundColor: "#358600",
                                color: "#fff",
                            },
                            padding: "dense",
                            toolbar: false,
                        }}
                        localization={{
                            body: {
                                emptyDataSourceMessage: `${t(
                                    "general.emptyDataMessageTable"
                                )}`,
                            },
                        }}
                    />
                    <TablePagination
                        align="left"
                        className="px-16"
                        rowsPerPageOptions={[1, 2, 3, 5, 10, 25, 50, 100]}
                        component="div"
                        labelRowsPerPage={t("general.rows_per_page")}
                        labelDisplayedRows={({ from, to, count }) =>
                            `${from}-${to} ${t("general.of")} ${
                                count !== -1 ? count : `more than ${to}`
                            }`
                        }
                        count={totalElements}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        backIconButtonProps={{
                            "aria-label": "Previous Page",
                        }}
                        nextIconButtonProps={{
                            "aria-label": "Next Page",
                        }}
                        onChangePage={this.handleChangePage}
                        onChangeRowsPerPage={this.setRowsPerPage}
                    />
                </Grid>
            </div>
        );
    }
}

export default User;
