//       window.location = 'session/signin';
//     }
//     if (err.response.status === 404) {
//       debugger;
//       toast.error("Hàm không tồn tại");
//     }
//     throw err;
//   }
// );

const App = () => {
  return (
    <AppContext.Provider value={{ routes }}>
      <Provider store={Store}>
        <EgretTheme>
          <Auth>
            <Router history={history}>
              <AuthGuard>
                <EgretLayout />
              </AuthGuard>
            </Router>
          </Auth>
        </EgretTheme>
      </Provider>
    </AppContext.Provider>
  );
};

export default App;
